package programing;

import java.util.Scanner;

public class multiplication {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner sc=new Scanner(System.in);
		int a=sc.nextInt();
		int i=1;
		while(i<=10)
		{
			System.out.println(a+"*"+i+"="+a*i);
			i++;
		}
		
	}

}
